  nad1 DATE; nad2 DATE; nad3 DATE; nav1 VARCHAR2( 50 CHAR ); nav2 VARCHAR2( 50 CHAR ); nav3 VARCHAR2( 50 CHAR ); nav4 VARCHAR2( 50 CHAR ); nav5 VARCHAR2( 50 CHAR ); nav6 VARCHAR2( 50 CHAR );
BEGIN
  DOC41WEB_FDT.CDT (
