    is_Insert			=> INSERTING,
    dest_OID_Schema_Name	=> 'DOC41WEB_FDT',
    dest_OID_Table_Name		=> 'WM_OBJ_STATS',
    old_Object_Id		=> :OLD.object_Id,
    new_Object_Id		=> :NEW.object_Id,
    -- optional set 1: created/changed
    old_Created			=> :OLD.created,
    new_Created			=> :NEW.created		/*nad1*/,
    old_Changed			=> :OLD.changed,
    new_Changed			=> :NEW.changed		/*nad2*/,
    -- optional set 1.1: userchanged
    --old_UserChanged		=> :OLD.userChanged,
    new_UserChanged		=> /*:NEW.userChanged*/	nad3,
    -- optional set 1.2: createdBy/changedBy
    --old_CreatedBy		=> :OLD.createdBy,
    new_CreatedBy		=> /*:NEW.createdBy*/	nav1,
    --old_ChangedBy		=> :OLD.changedBy,
    new_ChangedBy		=> /*:NEW.changedBy*/	nav2,
    -- optional set 1.2.1: createdOn/changedOn
    --old_CreatedOn		=> :OLD.createdOn,
    new_CreatedOn		=> /*:NEW.createdOn*/	nav3,
    --old_ChangedOn		=> :OLD.changedOn,
    new_ChangedOn		=> /*:NEW.changedOn*/	nav4,
    -- optional set 2: objectNo 
    --old_Object_No		=> :OLD.doc_No,
    new_Object_No		=> /*:NEW.doc_No*/	nav5,
    --object_No_Allow_Chg	=> true,
